@-webkit-keyframes neon-cursor-box {
  0% {
    outline: 1px solid #e6fcff;
  }
  80%,
  100% {
    outline: 1px solid #a3dcf5;
  }
}
@-webkit-keyframes neon-cursor-bar {
  0% {
    border-left: 1px solid #e6fcff;
  }
  80%,
  100% {
    border-left: 1px solid #a3dcf5;
  }
}
@-webkit-keyframes neon-cursor-glow {
  0% {
    box-shadow: 0 0 4px 0px #e6fcff, 0 0 4px 0px #e6fcff inset;
  }
  80%,
  100% {
    box-shadow: 0 0 4px 0px rgba(230, 252, 255, 0), 0 0 4px 0px rgba(230, 252, 255, 0) inset;
  }
}
@-webkit-keyframes neon-cursor-pulse {
  0% {
    -webkit-transform: scale(1);
    opacity: 1;
  }
  20%,
  100% {
    -webkit-transform: scale(1.5);
    opacity: 0;
  }
}
atom-text-editor:not(.insert-mode) .cursors.neon-cursor-bar > .cursor,
atom-text-editor::shadow:not(.insert-mode) .cursors.neon-cursor-bar > .cursor {
  -webkit-animation: neon-cursor-bar 1s linear infinite;
}
atom-text-editor:not(.insert-mode) .cursors.neon-cursor-bar > .cursor:after,
atom-text-editor::shadow:not(.insert-mode) .cursors.neon-cursor-bar > .cursor:after {
  width: 0px;
}
atom-text-editor:not(.insert-mode) .cursors.neon-cursor-bar > .cursor:before,
atom-text-editor::shadow:not(.insert-mode) .cursors.neon-cursor-bar > .cursor:before {
  left: -1px;
  width: 1px;
}
atom-text-editor:not(.insert-mode) .cursors > .cursor,
atom-text-editor::shadow:not(.insert-mode) .cursors > .cursor {
  border: none !important;
  -webkit-animation: neon-cursor-box 1s linear infinite;
}
atom-text-editor:not(.insert-mode) .cursors > .cursor:before,
atom-text-editor::shadow:not(.insert-mode) .cursors > .cursor:before {
  content: '';
  position: absolute;
  width: 100%;
  height: 100%;
  left: 0;
  top: 0;
  -webkit-animation: neon-cursor-glow 1s linear infinite;
}
atom-text-editor:not(.insert-mode) .cursors > .cursor:after,
atom-text-editor::shadow:not(.insert-mode) .cursors > .cursor:after {
  content: '';
  outline: 1px solid #e6fcff;
  width: 100%;
  height: 100%;
  position: absolute;
  left: 0;
  top: 0;
  transform-origin: center center;
  -webkit-animation: neon-cursor-pulse 1s linear infinite;
}
atom-text-editor:not(.insert-mode) .cursors.blink-off > .cursor,
atom-text-editor::shadow:not(.insert-mode) .cursors.blink-off > .cursor {
  opacity: 1 !important;
}
atom-text-editor.vim-mode:not(.insert-mode) .scroll-view .cursors .cursor,
atom-text-editor::shadow.vim-mode:not(.insert-mode) .scroll-view .cursors .cursor {
  background-color: inherit;
  border: 1px solid #a3dcf5;
  opacity: inherit;
  visibility: visible;
}
