css:
body {
  z-index: 0;
  background: #21252b;
}
.editor-backgroundatom-workspace {
  z-index: 2;
}
.editor-background atom-pane {
  background: transparent !important;
}
.editor-background atom-text-editor {
  background: transparent !important;
}
.editor-background atom-text-editor::shadow .lines {
  background: transparent !important;
}
.editor-background atom-text-editor::shadow .gutter {
  background: transparent !important;
}
.eb-modal-window {
  position: absolute;
  background: #21252b;
  color: #9da5b4;
  border: 1px solid #181a1f;
  min-width: 300px;
  left: 0px;
  top: 0px;
  opacity: 1;
  display: none;
  z-index: 999;
  text-align: center;
  border-radius: 3px;
  box-shadow: 0px 10px 40px rgba(0, 0, 0, 0.45);
  /*!
 * Bootstrap Colorpicker
 * http://mjolnic.github.io/bootstrap-colorpicker/
 *
 * Originally written by (c) 2012 Stefan Petre
 * Licensed under the Apache License v2.0
 * http://www.apache.org/licenses/LICENSE-2.0.txt
 *
 */
}
.eb-modal-window #working {
  float: left;
}
.eb-modal-window .wrapper {
  position: relative;
}
.eb-modal-window .title {
  background: #282c34;
  border-bottom: 1px solid #181a1f;
  padding: 10px;
  text-shadow: 0px 1px 0px rgba(0, 0, 0, 0.3);
}
.eb-modal-window .close {
  float: right;
  padding: 4px;
  margin: 0px 4px 0px 10px;
  line-height: 1.3;
}
.eb-modal-window .content {
  padding: 10px;
  /*background: linear-gradient(rgba(0,0,0,0.1),rgba(0,0,0,0) 40px);*/
}
.eb-modal-window .buttons {
  position: relative;
  bottom: 0px;
  margin-top: 13px;
  padding: 10px;
  border-top: 1px solid #181a1f;
  text-align: right;
  background: #21252b;
}
.eb-modal-window input,
.eb-modal-window textarea {
  background: #282c34;
  border: 1px solid #181a1f;
  color: #9da5b4;
  padding: 4px;
  border-radius: 3px;
  margin: 2px;
}
.eb-modal-window input[type="text"],
.eb-modal-window input[type="number"] {
  box-shadow: inset 2px 2px 2px rgba(0, 0, 0, 0.1);
}
.eb-modal-window input[type="text"] {
  min-width: 300px;
}
.eb-modal-window input[type="color"] {
  height: 40px;
  vertical-align: middle;
}
.eb-modal-window select {
  background: #21252b;
  border: 1px solid #181a1f;
  color: #9da5b4;
  border-radius: 3px;
  padding: 4px;
}
.eb-modal-window .group {
  margin: 6px 0px;
}
.eb-modal-window .group-addon:last-child {
  margin-left: 0px;
  border-left: none;
  margin-top: 0;
  border-radius: 0px 3px 3px 0px;
}
.eb-modal-window .slider {
  width: 160px;
  display: inline-block;
  vertical-align: middle;
}
.eb-modal-window .slider > .dragger {
  border: 1px solid #181a1f;
  width: 16px;
  height: 16px;
  border-radius: 3px;
  background: linear-gradient(rgba(255, 255, 255, 0.1), rgba(255, 255, 255, 0) 4px, rgba(255, 255, 255, 0) 10px, rgba(0, 0, 0, 0.33)), #282c34;
}
.eb-modal-window .slider > .track {
  box-shadow: inset 2px 2px 2px rgba(0, 0, 0, 0.2);
  height: 10px;
  border-radius: 3px;
  border: 1px solid #181a1f;
}
.eb-modal-window .slider > .track:before {
  content: "";
  width: 100%;
  height: 100%;
  position: absolute;
  background: repeating-linear-gradient(to right, #21252b 0px, #21252b 1px, #181a1f 2px, #181a1f 3px);
  background-size: 2px;
  opacity: 0.3;
}
.eb-modal-window .file-text {
  margin-right: 0px;
  border-right: none;
  border-top-right-radius: 0px;
  border-bottom-right-radius: 0px;
  vertical-align: middle;
  min-width: 300px;
}
.eb-modal-window .file-btn {
  margin-left: 0px;
  border-top-left-radius: 0;
  border-bottom-left-radius: 0;
  line-height: inherit;
  padding: 4px 10px;
}
.eb-modal-window label {
  min-width: 150px;
  border-bottom: 1px dotted rgba(157, 165, 180, 0.6);
}
.eb-modal-window .config-content {
  background: transparent;
  position: relative;
  clear: both;
}
.eb-modal-window .tab-content {
  text-align: left;
  display: none;
  padding: 10px;
  background: #21252b;
}
.eb-modal-window .tab-content.active {
  display: block;
  border: 1px solid #181a1f;
}
.eb-modal-window .tab {
  display: block;
  float: left;
  padding: 4px 10px;
  cursor: pointer;
  border: 1px solid #181a1f;
  border-right: none;
  border-bottom: none;
  background: #21252b;
}
.eb-modal-window .tab:last-child {
  border-right: 1px solid #181a1f;
}
.eb-modal-window .tab.active {
  font-weight: bold;
  position: relative;
  background: linear-gradient(#282c34, #21252b 50%);
}
.eb-modal-window .tab.active:after {
  content: '';
  position: absolute;
  width: 100%;
  height: 2px;
  background: #21252b;
  z-index: 1;
  left: 0;
  bottom: -2px;
}
.eb-modal-window .tab.active:before {
  content: '';
  position: absolute;
  width: 100%;
  height: 2px;
  background: red;
  z-index: 1;
  left: 0;
  top: -2px;
}
.eb-modal-window .config-tabs {
  overflow: visible;
  border-collapse: collapse;
}
.eb-modal-window .picker-wrapper {
  position: relative;
  display: inline-block;
}
.eb-modal-window .colorpicker-saturation {
  float: left;
  width: 100px;
  height: 100px;
  cursor: crosshair;
  background-image: url("atom://editor-background/images/saturation.png");
}
.eb-modal-window .colorpicker-saturation i {
  position: absolute;
  top: 0;
  left: 0;
  display: block;
  width: 5px;
  height: 5px;
  margin: -4px 0 0 -4px;
  border: 1px solid #000;
  -webkit-border-radius: 5px;
  -moz-border-radius: 5px;
  border-radius: 5px;
}
.eb-modal-window .colorpicker-saturation i b {
  display: block;
  width: 5px;
  height: 5px;
  border: 1px solid #fff;
  -webkit-border-radius: 5px;
  -moz-border-radius: 5px;
  border-radius: 5px;
}
.eb-modal-window .colorpicker-hue,
.eb-modal-window .colorpicker-alpha {
  float: left;
  width: 15px;
  height: 100px;
  margin-bottom: 4px;
  margin-left: 4px;
  cursor: row-resize;
}
.eb-modal-window .colorpicker-hue i,
.eb-modal-window .colorpicker-alpha i {
  position: absolute;
  top: 0;
  left: 0;
  display: block;
  width: 100%;
  height: 1px;
  margin-top: -1px;
  background: #000;
  border-top: 1px solid #fff;
}
.eb-modal-window .colorpicker-hue {
  background-image: url("atom://editor-background/images/hue.png");
}
.eb-modal-window .colorpicker-alpha {
  display: none;
  background-image: url("atom://editor-background/images/alpha.png");
}
.eb-modal-window .colorpicker-saturation,
.eb-modal-window .colorpicker-hue,
.eb-modal-window .colorpicker-alpha {
  background-size: contain;
}
.eb-modal-window .colorpicker {
  top: 100%;
  right: 0;
  z-index: 9999999999;
  min-width: 130px;
  padding: 4px;
  background: #1b1d23;
  position: absolute;
  margin-top: 1px;
  -webkit-border-radius: 4px;
  -moz-border-radius: 4px;
  border-radius: 4px;
  *zoom: 1;
}
.eb-modal-window .colorpicker:before,
.eb-modal-window .colorpicker:after {
  display: table;
  line-height: 0;
  content: "";
}
.eb-modal-window .colorpicker:after {
  clear: both;
}
.eb-modal-window .colorpicker:before {
  position: absolute;
  top: -7px;
  left: 6px;
  display: inline-block;
  border-right: 7px solid transparent;
  border-bottom: 7px solid #ccc;
  border-left: 7px solid transparent;
  border-bottom-color: rgba(0, 0, 0, 0.2);
  content: '';
}
.eb-modal-window .colorpicker:after {
  position: absolute;
  top: -6px;
  left: 7px;
  display: inline-block;
  border-right: 6px solid transparent;
  border-bottom: 6px solid #1b1d23;
  border-left: 6px solid transparent;
  content: '';
}
.eb-modal-window .colorpicker div {
  position: relative;
}
.eb-modal-window .colorpicker.colorpicker-with-alpha {
  min-width: 140px;
}
.eb-modal-window .colorpicker.colorpicker-with-alpha .colorpicker-alpha {
  display: block;
}
.eb-modal-window .colorpicker-color {
  height: 10px;
  margin-top: 5px;
  clear: both;
  background-image: url("atom://editor-background/images/alpha.png");
  background-position: 0 100%;
}
.eb-modal-window .colorpicker-color div {
  height: 10px;
}
.eb-modal-window .colorpicker-element .input-group-addon i,
.eb-modal-window .colorpicker-element .add-on i {
  display: inline-block;
  width: 16px;
  height: 16px;
  vertical-align: text-top;
  cursor: pointer;
}
.eb-modal-window .colorpicker.colorpicker-inline {
  position: relative;
  z-index: auto;
  display: inline-block;
  float: none;
}
.eb-modal-window .colorpicker.colorpicker-horizontal {
  width: 110px;
  height: auto;
  min-width: 110px;
}
.eb-modal-window .colorpicker.colorpicker-horizontal .colorpicker-saturation {
  margin-bottom: 4px;
}
.eb-modal-window .colorpicker.colorpicker-horizontal .colorpicker-color {
  width: 100px;
}
.eb-modal-window .colorpicker.colorpicker-horizontal .colorpicker-hue,
.eb-modal-window .colorpicker.colorpicker-horizontal .colorpicker-alpha {
  float: left;
  width: 100px;
  height: 15px;
  margin-bottom: 4px;
  margin-left: 0;
  cursor: col-resize;
}
.eb-modal-window .colorpicker.colorpicker-horizontal .colorpicker-hue i,
.eb-modal-window .colorpicker.colorpicker-horizontal .colorpicker-alpha i {
  position: absolute;
  top: 0;
  left: 0;
  display: block;
  width: 1px;
  height: 15px;
  margin-top: 0;
  background: #ffffff;
  border: none;
}
.eb-modal-window .colorpicker.colorpicker-hidden {
  display: none;
}
.eb-modal-window .colorpicker.colorpicker-visible {
  display: block;
}
.eb-modal-window .colorpicker-inline.colorpicker-visible {
  display: inline-block;
}
.eb-modal-window .colorpicker-right:before {
  right: 6px;
  left: auto;
}
.eb-modal-window .colorpicker-right:after {
  right: 7px;
  left: auto;
}
atom-pane-container atom-pane .item-views {
  background: transparent !important;
  background-color: transparent !important;
}
atom-workspace.editor-background {
  background: transparent !important;
}
